import { useContext } from "react"
import { useState } from "react";
import DataContext from './context/DataContext';
import api from './api/posts';


const ImageUpload = ({ pet_id }) => {
    const { userID, auth } = useContext(DataContext);
    const [image, setImage] = useState();

    const handleImage = (e) => {
        console.log(e.target.files[0])
        setImage(e.target.files[0])
    }

    const handleUpload = async (e) => {
        const response = await api.post(`${userID}/pets/${pet_id}/upload`, image, {
            headers: {
                'x-access-token': auth.accessToken
            }
        })
        console.log(response.data)
    }

    return (
        <div>
            <input type="file" name='file' onChange={(e) => handleImage(e)}/>
            <button type="submit" onSubmit={handleUpload}>Upload</button>
        </div>
    )
}

export default ImageUpload
