import { Link } from 'react-router-dom';

const Post = ({ pet }) => {
    return (
        <article className="post">
            <Link to={`post/${pet.id}`}>
                <h2>{pet.pet_name}</h2>
                <p className="postDate">Age: {pet.pet_age} years old</p>
            </Link>
            <ul className="postBody">{
                (pet.pet_description).length <= 25
                    ? pet.pet_description
                    : `${(pet.pet_description).slice(0, 25)}...`
            }</ul>
            {/* if the post.body length has <= 25 chars > show all, if not, only show the snippet of the first 25 characters */}
        </article>
    )
}

export default Post
