import { useParams, Link } from "react-router-dom";
import styled from "styled-components";
import { useContext, useEffect } from 'react';
import DataContext from './context/DataContext';
import ImageUpload from "./ImageUpload";
import api from './api/posts';

// -------------- CSS Style -------------- //
const Button1 = styled.button`
    height: 48px;
    min-width: 48px;
    margin-left: 0.5rem;
    border-radius: 0.25rem;
    padding: 0.5rem;
    font-size: 1rem;
    background-color: #ff5e6c;
    color: white;
    cursor: pointer;
`;

const Button2 = styled.button`
    height: 48px;
    min-width: 48px;
    margin-left: 0.5rem;
    border-radius: 0.25rem;
    padding: 0.5rem; 
    font-size: 1rem;
    background-color: #fff5d7;
    color: black;
    cursor: pointer;
`;
const PostPage = () => {
    const { pets, handleDelete, sendEmail, userID, auth, picURL, setPicURL } = useContext(DataContext);
    const { id } = useParams(); // id is used in Route
    // specify the post we want to see by finding the post.id
    const pet = pets.find(pet => (pet.id).toString() === id); // === is for string match, == for numeric
    useEffect(() => {
        const getImages = async (id) => {
            try {
                // in axios, it automatically returns response in json format and catch the error, accesible through response.data
                const response = await api.get(`${userID}/pets/${id}/upload`, {
                    headers: {
                        'x-access-token': auth.accessToken
                    }
                });
                setPicURL(response.data); // response.data is in json format
            } catch (err) {
                if (err.response) { // Not in the 200 response range
                    console.log(err.response.data);
                    console.log(err.response.status);
                    console.log(err.response.headers);
                } else { console.log(`Error: ${err.message}`) }
            }
        }
        getImages(id);
    }, [id]) // [] to show dependency so it won't infinitely request from server

    return (
        <main className="PostPage">
            <article className="post">
                {/* if post is true then && ... */}
                <div class = "column">
                {pet &&
                    <>
                        <h2>{pet.pet_name}</h2>
                        <li className="postBody">Birthday: {pet.pet_dob.slice(0, 16)}</li>
                        <li className="postBody">Last deworm date: {pet.last_deworm.slice(0, 16)}</li>
                        <li className="postBody">Age: {pet.pet_age}</li>
                        <li className="postBody">Sterilised: {(pet.sterilised).toString()}</li>
                        <li className="postBody">Pet Type: {pet.pet_type}</li>
                        <li className="postBody">Owner: {pet.owner}</li>
                        <li className="postBody">Description: {pet.pet_description}</li>

                        <Button1 onClick={() => handleDelete(pet.id)}>
                            Remove Pet
                        </Button1>

                        <Link to={`edit`}><Button2>Edit Pet</Button2></Link>

                        {/* <Button2 onClick={() => sendEmail(pet.id)}>
                            Notify me
                        </Button2> */}
                    </>
                }
                {!pet &&
                    <>
                        <h2>Pet Not Found</h2>
                        <p>
                            <Link to='/'>Return to Homepage</Link>
                        </p>
                    </>
                }
                </div>
                <div class="column">
                    {/* Placeholder for image */}
                    <h1>Profile Picture</h1>
                    <img src={pet.profile_pic} />
                </div>

                <ImageUpload pet_id = {pet.id}/>
            </article>

        </main>
    )
}

export default PostPage
