import { Link } from 'react-router-dom';
import Loki from './images/Loki2.jpeg'

const Post = ({ pet }) => {
    return (
        <div className="post">
            <Link to={`post/${pet.id}`}>
                <h2>{pet.pet_name}</h2>
            </Link>

            <div>
                <img src={Loki} alt={pet.pet_name}/>
            </div>

            <div className="postDate">
                {pet.pet_type}
            </div>

            {/* <p className="postBody">{
                (pet.pet_description).length <= 25
                    ? pet.pet_description
                    : `${(pet.pet_description).slice(0, 25)}...`
            }</p> */}
            {/* if the post.body length has <= 25 chars > show all, if not, only show the snippet of the first 25 characters */}
        </div>    
    )
}

export default Post
