import { useParams, Link } from "react-router-dom";
import styled from "styled-components";
import { useContext } from 'react';
import DataContext from './context/DataContext';
import reactImage from "./images/Loki2.jpeg";

// -------------- CSS Style -------------- //
const Button1 = styled.button`
    height: 48px;
    min-width: 48px;
    margin-left: 0.5rem;
    border-radius: 0.25rem;
    padding: 0.5rem;
    font-size: 1rem;
    background-color: #ff5e6c;
    color: white;
    cursor: pointer;
`;

const Button2 = styled.button`
    height: 48px;
    min-width: 48px;
    margin-left: 0.5rem;
    border-radius: 0.25rem;
    padding: 0.5rem; 
    font-size: 1rem;
    background-color: #fff5d7;
    color: black;
    cursor: pointer;
`;
const data =
"";

const PostPage = () => {
    const { pets, handleDelete, sendEmail } = useContext(DataContext);
    const { id } = useParams(); // id is used in Route
    // specify the post we want to see by finding the post.id
    const pet = pets.find(pet => (pet.id).toString() === id); // === is for string match, == for numeric
    return (
        <main className="PostPage">
            <article className="post">
                {/* if post is true then && ... */}
                <div class = "column">
                {pet &&
                    <>
                        <h2>{pet.pet_name}</h2>
                        <li className="postBody">Birthday: {pet.pet_dob.slice(0, 16)}</li>
                        <li className="postBody">Last deworm date: {pet.last_deworm.slice(0, 16)}</li>
                        <li className="postBody">Age: {pet.pet_age}</li>
                        <li className="postBody">Sterilised: {(pet.sterilised).toString()}</li>
                        <li className="postBody">Pet Type: {pet.pet_type}</li>
                        <li className="postBody">Owner: {pet.owner}</li>
                        <Button1 onClick={() => handleDelete(pet.id)}>
                            Remove Pet
                        </Button1>

                        <Link to={`edit`}><Button2>Edit Pet</Button2></Link>

                        <Button2 onClick={() => sendEmail(pet.id)}>
                            Notify me
                        </Button2>
                    </>
                }
                {!pet &&
                    <>
                        <h2>Pet Not Found</h2>
                        <p>
                            <Link to='/'>Return to Homepage</Link>
                        </p>
                    </>
                }
                </div>
                <div class="column">
                    {/* Placeholder for image */}
                    {/* <img src={reactImage} alt="react logo" /> */}
                    <h1>base64 string image</h1>
                    <img src={`data:image/jpeg;base64,${data}`} />
                </div>

            </article>

        </main>
    )
}

export default PostPage
