import { Link } from 'react-router-dom';
import Loki from './images/Loki2.jpeg'

const Post = ({ pet }) => {
    return (
        <div className="movie">
            <div></div>

            <div>
                <img src={Loki} alt={pet.pet_name}/>
            </div>

            <div>
                <Link to={`post/${pet.id}`}>
                    <h3>{pet.pet_name}</h3>
                </Link>
                <span>{pet.pet_type}</span>
            </div>

            {/* <p className="postBody">{
                (pet.pet_description).length <= 25
                    ? pet.pet_description
                    : `${(pet.pet_description).slice(0, 25)}...`
            }</p> */}
        </div>    
    )
}

export default Post
