import Layout from './Layout';
import Home from './Home';
import NewPet from './NewPet';
import PostPage from './PostPage';
import About from './About';
import Missing from './Missing';
import { Route, Routes, useNavigate } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { format } from 'date-fns';
import apiRequest from './apiRequest';

function App() {
  const API_URL = 'http://127.0.0.1:5000/pets/';
  const [pets, setPets] = useState([]); /* default state is an array loaded from local storage or an empty array, in the event the database shoppinglist is deleted */
  const [search, setSearch] = useState('');
  const [fetchError, setFetchError] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [searchResults, setSearchResults] = useState([]); // array of search results
  const navigate = useNavigate();

  // Inputs for Pet in POST
  const [petName, setPetName] = useState('');
  const [petType, setPetType] = useState('');
  const [dob, setDob] = useState('');
  const [deworm, setDeworm] = useState(format(new Date(), 'M-d-y'));
  const [gender, setGender] = useState('');
  const [ster, setSter] = useState(false);
  const [petDesc, setPetDesc] = useState('');

  const addPet = async (petName, petType, dob, deworm, gender, ster, petDesc) => {
    const id = pets.length ? pets[pets.length - 1].id + 1 : 1; // to reference the last post --> posts[posts.length-1]
    const isSterilised = (ster.toLowerCase()) === 'true';
    const newPet = { 
      id, 
      pet_name: petName, 
      pet_type: petType,
      pet_dob: dob,
      pet_gender: gender,
      last_deworm: deworm,
      sterilised: isSterilised, 
      pet_description: petDesc,
      user_id: 7 };
    const allPets = [...pets, newPet]; // append new post to allPosts
    setPets(allPets); // set Posts to allPosts
    console.log(JSON.stringify(newPet));

    /* CREATE in CRUD*/
    const postOptions = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(newPet)
    };
    const result = await apiRequest(`${API_URL}register`, postOptions);
    if (result) setFetchError(result);
  }
  /* REQUEST in CRUD - used in useEffect */
  async function fetchPets(){
    try {
      // const response = await fetch(API_URL, {method: "GET"});
      const response = await fetch(API_URL, {method: "GET"});
      if (!response.ok) throw Error('Did not receive expected data');
      const listPets = await response.json();
      setPets(listPets);
      setFetchError(null);
      // console.log(listPets)
    } catch (err) {
      setFetchError(err.message);
    } finally {
      setIsLoading(false); /* tracking the state of IsLoading */
    }
  }

  useEffect(() => {
    fetchPets();
    setTimeout(() => {
      (async () => await fetchPets())();
    }, 2000);
  }, []) // [] to show dependency so it won't infinitely request from server

  useEffect(() => {
    const filteredResults = pets.filter((pet) =>
    ((pet.pet_name).toLowerCase()).includes(search.toLowerCase()));

    setSearchResults(filteredResults.reverse());
  }, [pets, search]) 

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!petName) return;
    addPet(petName, petType, dob, deworm, gender, ster, petDesc) // addItem function
    setPetName(''); // reset the setPostTitle and setPostBody to empty
    setPetType('');
    setDob('');
    setDeworm(format(new Date(), 'M-d-y'));
    setGender('');
    setSter(false);
    setPetDesc('');
    navigate('/'); // return to home page after submitting post
  }

  const handleDelete = async (id) => {
    // filter out the posts with post.id not equal to the id of the post we want to delete
    const petsList = pets.filter(pet => pet.id !== id);
    setPets(petsList); // set the Posts to the newly filtered postsList

    /* DELETE in CRUD */
    const deleteOptions = {method: 'DELETE'}
    const reqUrl = `${API_URL}${id}` /* Access the specific item */
    const result = await apiRequest(reqUrl, deleteOptions)
    if (result) setFetchError(result);

    navigate('/');
  }

  const sendEmail = async (id) => {
    try {
      const response = await fetch(`${API_URL}reminder/${id}/send-email`, {method: "GET"});
      if (!response.ok) throw Error('Did not receive expected data');
    } catch (err) {
      setFetchError(err.message);
    } finally {
      setIsLoading(false); /* tracking the state of IsLoading */
    }

    navigate('/');
  }

  return (
    <Routes>
      <Route path="/" element={<Layout
        search={search}
        setSearch={setSearch}
      />}>
        <Route index element={<Home pets={searchResults} />} />
        <Route path="post">
          <Route index element={<NewPet
            handleSubmit={handleSubmit}
            petName={petName} setPetName={setPetName}
            petType={petType} setPetType={setPetType}
            dob={dob} setDob={setDob}
            deworm={deworm} setDeworm={setDeworm}
            gender={gender} setGender={setGender}
            ster={ster} setSter={setSter}
            petDesc={petDesc} setPetDesc={setPetDesc}
          />} />
          <Route path=":id" element={<PostPage
            pets={pets}
            handleDelete={handleDelete}
            sendEmail={sendEmail}
          />} />
        </Route>
        <Route path="about" element={<About />} />
        <Route path="*" element={<Missing />} />
      </Route>
    </Routes>
  );
}

export default App;
