import { useEffect } from "react";
import { useParams, Link } from "react-router-dom";

export const EditPet = ({
    pets, handleEdit, newPetName, setNewPetName
}) => {

    const { id } = useParams();
    const pet = pets.find(pet => (pet.id).toString() === id);
    console.log(pet)
    useEffect(() => {
        if (pet) {
            setNewPetName(newPetName);
        }
    }, [pet, newPetName, setNewPetName])

    return (
        <main className="NewPost">
            {newPetName &&
                <>
                    <h2>New Pet</h2>
                    <form className="newPostForm" onSubmit={(e)=>e.preventDefault()}>
                        <label htmlFor="postTitle">Pet Name:</label>
                        <input
                            id="postTitle" // match with htmlFor attribute
                            type="text" // accept text input
                            required
                            value={newPetName}
                            onChange={(e) => setNewPetName(e.target.value)}
                        />
                        <button type="submit" onClick={()=>handleEdit(pet.id)}>Submit</button>
                    </form>
                </>
            }

            {!newPetName && 
                <>
                    <h2>Page Not Found</h2>
                    <p>Well, that's disappointing.</p>
                    <p>
                        <Link to='/'>Visit Our Homepage</Link>
                    </p>
                </>
            }
        </main>
    )
}

export default EditPet
