import Feed from './Feed';

const Home = ({ posts }) => {
    return (
        <main className="Home">
            {/* check the posts length - if there are posts > Feed, if not, show "No Posts to display" */}
            {posts.length ? (
                <Feed posts={posts} />
            ) : (
                <p style={{ marginTop: "2rem" }}>
                    No posts to display.
                </p>
            )}
        </main>
    )
}

export default Home
