import { Link } from 'react-router-dom';
import { useContext, useEffect } from 'react';
import DataContext from './context/DataContext';
import ImageUpload from "./ImageUpload";
import api from './api/posts';

const Post = ({ pet }) => {
    const { userID, auth, picURL, setPicURL } = useContext(DataContext);
    useEffect(() => {
        const getImages = async (id) => {
            try {
                // in axios, it automatically returns response in json format and catch the error, accesible through response.data
                const response = await api.get(`${userID}/pets/${id}/upload`, {
                    headers: {
                        'x-access-token': auth.accessToken
                    }
                });
                setPicURL(response.data); // response.data is in json format
            } catch (err) {
                if (err.response) { // Not in the 200 response range
                    console.log(err.response.data);
                    console.log(err.response.status);
                    console.log(err.response.headers);
                } else { console.log(`Error: ${err.message}`) }
            }
        }
        getImages(pet.id);
    }, [pet.id]) // [] to show dependency so it won't infinitely request from server

    return (
        <article className="post">
            <Link to={`post/${pet.id}`}>
                <h2>{pet.pet_name}</h2>
                <p className="postDate">Pet Type: {pet.pet_type}</p>
            </Link>
            <p className="postBody">{
                (pet.pet_description).length <= 25
                    ? pet.pet_description
                    : `${(pet.pet_description).slice(0, 25)}...`
            }</p>
            {/* if the post.body length has <= 25 chars > show all, if not, only show the snippet of the first 25 characters */}
        </article>
    )
}

export default Post
